import api from '@/services/authentication';  // наш axios‑инстанс



const state = {
  products: [],
};

const mutations = {
  initializeStore(state, newData) {
    state.products = newData;
  },
};

const actions = {

  async updateDriverNightStatus(_, { driverId, isNight }) {
    try {
      await api.post(`/night/${driverId}`, { isNight });
    } catch (err) {
      console.error("Ошибка при обновлении режима 'ночь/день':", err);
    }
  },


  async loadVehicle({ commit }) {
    try {


      const response = await api.get("/api/info/card");
      commit("initializeStore", response.data);
    }
    catch (err) {
      console.log(err);
      if (err.response && err.response.status === 401) {
        // this.$router.push('/login');

      }
    }
  },

  async addVehicle({ dispatch }, payload) {
    const { isNight, ...driverData } = payload;

    await api.post("http://localhost:3000/api/info/card", payload);
    dispatch("loadVehicle");
  },

  async removeVehicle({ dispatch }, id) {
    await api.delete(`http://localhost:3000/api/info/card/${id}`);
    dispatch("loadVehicle");
  },
};

const getters = {
  getVehicleInfo(state) {
    return state.products;
  },
};

export default {
  namespaced: true,
  state,
  mutations,
  actions,
  getters,
};
