import "core-js/modules/es.array.push.js";
import { login } from '@/services/authentication';
export default {
  data() {
    return {
      email: '',
      password: '',
      error: '',
      loading: false
    };
  },
  methods: {
    async onLogin() {
      this.loading = true;
      this.error = '';
      try {
        await login({
          email: this.email,
          password: this.password
        });
        this.$router.push('/home');
      } catch (err) {
        this.error = err.response.data.message || "Ошибка";
      } finally {
        this.loading = false;
      }
    }
  }
};
